mapper.select:
select * from employee 
where emp_id=#{emp_id} 
and emp_pw=#{emp_pw}

mapper.update:
    update employee 
    set emp_id=#{emp_id}, emp_name=#{emp_name}, emp_birth=#{emp_birth}, emp_gender=#{emp_gender}, emp_tel=#{emp_tel}, emp_email=#{emp_email},
    emp_addr=#{emp_addr}, emp_dnum=#{emp_dnum}, emp_position=#{emp_position}, emp_job=#{emp_job}, emp_status=#{emp_status}, emp_bnum=#{emp_bnum},
	emp_work_type=#{emp_work_type}, emp_bank_name=#{emp_bank_name}, emp_account_num=#{emp_account_num}, emp_account_name=#{emp_account_name}, emp_start_date=#{emp_start_date}	    

    where emp_id=#{emp_id}

mapper.select:
SELECT emp_account_name, emp_account_num, emp_bank_name
FROM employee
WHERE emp_id = #{emp_id}

SELECT rid, division, rname, reason, rdate
FROM reward
WHERE emp_id = #{emp_id}

SELECT eval_name, score1, score2, score3, total, feedback, valuator, eval_date
FROM eval
WHERE emp_id = #{emp_id}

mapper.update:
UPDATE employee
SET emp_account_name = #{emp_account_name},
    emp_account_num = #{emp_account_num},
    emp_bank_name = #{emp_bank_name}
WHERE emp_id = #{emp_id}
